import React, { useState, useEffect } from 'react';
import useWebSocket from '../hooks/useWebSocket';

const Lobby = () => {
    const [username, setUsername] = useState('');
    const [challengePlayer, setChallengePlayer] = useState('');
    const [message, setMessage] = useState('');
    const [isChallenged, setIsChallenged] = useState(false);

    const { players, sendMessage } = useWebSocket('ws://localhost:5001', username);

    useEffect(() => {
        // Reset challenge state if a challenge is accepted or declined
        if (message && message.includes('Challenge received')) {
            setIsChallenged(true);
        } else {
            setIsChallenged(false);
        }
    }, [message]);

    const handleChallenge = (player) => {
        setChallengePlayer(player);
        sendMessage({ type: 'challenge', challengedPlayer: player });
    };

    const handleUsernameChange = (e) => {
        setUsername(e.target.value);
    };

    const handleChallengeResponse = (response) => {
        sendMessage({
            type: 'challengeResponse',
            response,
            challenger: challengePlayer
        });
        setChallengePlayer('');
    };

    return (
        <div>
            <h1>Lobby</h1>
            {!username ? (
                <div>
                    <label>Enter your username: </label>
                    <input
                        type="text"
                        value={username}
                        onChange={handleUsernameChange}
                        placeholder="Your username"
                    />
                </div>
            ) : (
                <div>
                    <h2>Welcome, {username}!</h2>
                    <h3>Players in Lobby:</h3>
                    <ul>
                        {players.length > 0 ? (
                            players.map((player) => (
                                <li key={player}>
                                    {player}{' '}
                                    <button onClick={() => handleChallenge(player)}>
                                        Challenge
                                    </button>
                                </li>
                            ))
                        ) : (
                            <p>No other players in the lobby.</p>
                        )}
                    </ul>
                </div>
            )}

            {isChallenged && (
                <div>
                    <p>{message}</p>
                    <button onClick={() => handleChallengeResponse('accept')}>
                        Accept Challenge
                    </button>
                    <button onClick={() => handleChallengeResponse('decline')}>
                        Decline Challenge
                    </button>
                </div>
            )}

            {message && !isChallenged && <p>{message}</p>}
        </div>
    );
};

export default Lobby;
