import React, {useState} from "react";

const GameBoard = () => {
    const gridSize = 5;
    const boxSize = 100;
    const padding = 20;

    const [lines, setLines] = useState({
        vertical: Array(gridSize).fill(Array(gridSize + 1).fill(false)),
        horizontal: Array(gridSize + 1).fill(Array(gridSize).fill(false))
    });

    const [completedBoxes, setCompletedBoxes] = useState(
        Array(gridSize - 1)
            .fill(null)
            .map(() => Array(gridSize - 1).fill(false))
    );

    const handleClick = (type, row, col) => {
        const newLines = {...lines};

        // mark line on click
        if (type === "vertical") {
            newLines.vertical[row] = [...newLines.vertical[row]];
            newLines.vertical[row][col] = !newLines.vertical[row][col];
        } else {
            newLines.horizontal[row] = [...newLines.horizontal[row]]; // Copy the row of horizontal lines
            newLines.horizontal[row][col] = !newLines.horizontal[row][col];
        }

        setLines(newLines);
    };

    return (
        <div style={{padding}}>
            <svg width={gridSize * boxSize + 2 * padding} height={gridSize * boxSize + 2 * padding} style={{border: "1px solid black"}}>
                {/* dots */}
                {Array.from({length: gridSize + 1}).map((_, row) =>
                    Array.from({length: gridSize + 1}).map((_, col) => (
                        <circle 
                            key={`dot-${row}-${col}`}
                            cx={col * boxSize + padding}
                            cy={row * boxSize + padding}
                            r="3"
                            fill="black"
                        />
                    ))
                )}

                {/* horizontal lines */}
                {lines.horizontal.map((row, rowIndex) =>
                    row.map((isClicked, colIndex) => (
                        <line
                            key={`hline-${rowIndex}-${colIndex}`}
                            x1={colIndex * boxSize + padding}
                            y1={rowIndex * boxSize + padding}
                            x2={(colIndex + 1) * boxSize + padding}
                            y2={rowIndex * boxSize + padding}
                            stroke={isClicked ? "blue" : "transparent"}
                            strokeWidth="5"
                            onMouseEnter={(e) => e.target.setAttribute("stroke", "gray")}
                            onMouseLeave={(e) => e.target.setAttribute("stroke", isClicked ? "blue" : "transparent")}
                            onClick={() => handleClick("horizontal", rowIndex, colIndex)}
                        />
                    ))
                )}

                {/* vertical lines */}
                {lines.vertical.map((row, rowIndex) =>
                    row.map((isClicked, colIndex) => (
                        <line 
                            key={`vline-${rowIndex}-${colIndex}`}
                            x1={colIndex * boxSize + padding}
                            y1={rowIndex * boxSize + padding}
                            x2={colIndex * boxSize + padding}
                            y2={(rowIndex + 1) * boxSize + padding}
                            stroke={isClicked ? "blue" : "transparent"}
                            strokeWidth="5"
                            onMouseEnter={(e) => e.target.setAttribute("stroke", "gray")}
                            onMouseLeave={(e) => e.target.setAttribute("stroke", isClicked ? "blue" : "transparent")}
                            onClick={() => handleClick("vertical", rowIndex, colIndex)}
                        />
                    ))
                )}
            </svg>
        </div>
    );
};

export default GameBoard;
