import React, { useState } from 'react';
import ChatBox from './chatBox';
import ChatInput from './chatInput';

const ChatContainer = ({ players, room, sendMessage, currentRoom }) => {
  const [currentMessage, setCurrentMessage] = useState('');

  const handleSendMessage = () => {
    if (currentMessage.trim()) {
      sendMessage({ type: 'chatMessage', message: currentMessage, room: currentRoom });
      setCurrentMessage('');
    }
  };

  return (
    <div className="chat-container">
      <h2>{currentRoom === 'lobby' ? 'Lobby Chat' : `Game Room Chat: ${currentRoom}`}</h2>
      <ChatBox players={players} currentRoom={currentRoom} />
      <ChatInput
        currentMessage={currentMessage}
        setCurrentMessage={setCurrentMessage}
        handleSendMessage={handleSendMessage}
      />
    </div>
  );
};

export default ChatContainer;
